import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import Typography from "@mui/material/Typography";
import Container from "@mui/material/Container";
import Box from "@mui/material/Box";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import { useSelector, useDispatch } from "react-redux";
import { RootState } from "../redux/store";
import { ToastContainer, toast } from "react-toastify";
import FavoriteButton from "../utils/FavouriteButton/FavoriteButton";
import UserComments from "../components/comments/UserComments";
import CommentInput from "../components/comments/CommentInput";
import RatingDisplayComponent from "../components/Rating/RatingDisplayComponent";
import {
  addRating,
  addComment,
  fetchComments,
  fetchRatings,
} from "../redux/slices/moviesSlice";
import { Movie } from "../utils/interface/types";
import { AppDispatch } from "../redux/store";
import { AxiosError } from "axios";

const MoviesDetailPage: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const movieId = id || "";

  const movie = useSelector((state: RootState) =>
    state.movies.movies.find((m) => m._id === movieId)
  ) as Movie;

  const isLoggedIn = useSelector((state: RootState) => state.user.isLoggedIn);
  const userDetails = useSelector((state: RootState) => state.user.userDetails);
  const userId = userDetails?.id || "";
  const userName = userDetails?.name || "";

  const dispatch = useDispatch<AppDispatch>();
  const [comments, setComments] = useState<
    { userId: string; userName: string; comment: string }[]
  >([]);

  const [userRatings, setUserRatings] = useState<
    { userId: string; userName: string; rating: number }[]
  >([]);

  useEffect(() => {
    if (!movieId) return;

    dispatch(fetchComments(movieId))
      .unwrap()
      .then((data) => setComments(data.comments))
      .catch((error) => {
        const axiosError = error as AxiosError;
        toast.error(axiosError.message || "Failed to fetch comments");
      });

    dispatch(fetchRatings(movieId))
      .unwrap()
      .then((data) => setUserRatings(data.ratings))
      .catch((error) => {
        const axiosError = error as AxiosError;
        toast.error(axiosError.message || "Failed to fetch ratings");
      });
  }, [movieId, dispatch]);

  const handleRatingClick = async (value: number) => {
    if (isLoggedIn) {
      try {
        await dispatch(addRating({ movieId, rating: value, userId, userName }));
        toast.success("Rating added successfully");
        // Refresh user ratings after adding new rating
        dispatch(fetchRatings(movieId))
          .unwrap()
          .then((data) => setUserRatings(data.ratings))
          .catch((error) => {
            const axiosError = error as AxiosError;
            toast.error(axiosError.message || "Failed to fetch ratings");
          });
      } catch (error) {
        const axiosError = error as AxiosError;
        toast.error(axiosError.message || "Failed to add rating");
      }
    } else {
      toast.error("You must be logged in to rate movies.");
    }
  };

  const handleCommentSubmit = async (comment: string) => {
    if (isLoggedIn) {
      try {
        const resultAction = await dispatch(
          addComment({ movieId, comment, userId, userName })
        ).unwrap();

        setComments([
          ...comments,
          { userId, userName, comment: resultAction.comment.comment },
        ]);

        toast.success("Comment added successfully");
      } catch (error) {
        const axiosError = error as AxiosError;
        toast.error(axiosError.message || "Failed to add comment");
      }
    } else {
      toast.error("You must be logged in to add comments.");
    }
  };

  return (
    <Container>
      <Box mb={2}>
        <Card>
          <CardMedia
            component="img"
            height="400"
            image={movie.poster || ""}
            alt={movie.title}
          />
          <CardContent>
            <Typography variant="h4">{movie.title}</Typography>
            <Typography variant="h6">{movie.year}</Typography>
            <Typography variant="body1">{movie.description}</Typography>
            <FavoriteButton movieId={movieId} />
          </CardContent>
        </Card>
      </Box>

      <Box mb={2}>
        <RatingDisplayComponent
          movieId={movieId}
          onRatingClick={handleRatingClick}
          onRatingsFetched={setUserRatings}
        />
      </Box>

      <Box mb={2}>
        <CommentInput onSubmit={handleCommentSubmit} />
      </Box>

      <Box mb={2}>
        <List>
          {comments.map((comment, index) => (
            <ListItem key={index}>
              <Typography variant="body2">
                <strong>{comment.userName}</strong>: {comment.comment}
              </Typography>
            </ListItem>
          ))}
        </List>
      </Box>

      <ToastContainer />
    </Container>
  );
};

export default MoviesDetailPage;
