import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { RootState, AppDispatch } from "../../redux/store";
import { fetchRatings } from "../../redux/slices/moviesSlice";
import RatingComponent from "./RatingComponent";

interface RatingDisplayComponentProps {
  movieId: string;
  handleRatingClick: (value: number) => void;
  onRatingsFetched: (
    ratings: { userId: string; userName: string; rating: number }[]
  ) => void;
}

const RatingDisplayComponent: React.FC<RatingDisplayComponentProps> = ({
  movieId,
  handleRatingClick,
  onRatingsFetched,
}) => {
  const dispatch: AppDispatch = useDispatch();
  const { isLoggedIn, userDetails } = useSelector(
    (state: RootState) => state.user
  );
  const ratings = useSelector((state: RootState) => state.movies.Rating);

  useEffect(() => {
    const fetchRatingsData = async () => {
      try {
        const response = await dispatch(fetchRatings(movieId)).unwrap();
        onRatingsFetched(response.ratings);

        // Fetch the user's rating for the movie
        // Assuming response contains the current user's rating for this movie
        const userRating = response.ratings.find(
          (rating) => rating.userId === userDetails?.id
        );
        if (userRating) {
          setInitialRating(userRating.rating);
        }
      } catch (error) {
        console.error("Failed to fetch ratings", error);
      }
    };

    fetchRatingsData();
  }, [movieId, dispatch, onRatingsFetched, userDetails?.id]);

  const [initialRating, setInitialRating] = React.useState<number | null>(null);

  return (
    <RatingComponent
      isLoggedIn={isLoggedIn}
      initialRating={initialRating}
      movieId={movieId}
      userId={userDetails?.id || ""}
      userName={userDetails?.name || ""}
      handleRatingClick={handleRatingClick}
    />
  );
};

export default RatingDisplayComponent;
